import React, { useState, useEffect } from 'react';
import CommonSection from '../shared/CommonSection';

import '../styles/tour.css';
import TourCard from './../shared/TourCard';
import SearchBar from './../shared/SearchBar';
import Newsletter from './../shared/Newsletter';
import TourData from '../assets/data/tours';
import { Container, Row, Col } from 'reactstrap';

const Tours = () => {
  const toursPerPage = 4; // Number of tours per page
  const [pageCount, setPageCount] = useState(0);
  const [page, setPage] = useState(0);
  const [currentTours, setCurrentTours] = useState([]);

  useEffect(() => {
    const pages = Math.ceil(TourData.length / toursPerPage); // Dynamic page count
    setPageCount(pages);

    // Slicing the tours based on the current page
    const startIndex = page * toursPerPage;
    const endIndex = startIndex + toursPerPage;
    setCurrentTours(TourData.slice(startIndex, endIndex));
  }, [page]);

  return (
    <>
      <CommonSection title={'All Tours'} />

      <section>
        <Container>
          <Row>
            <SearchBar />
          </Row>
        </Container>
      </section>

      <section className='pt-0'>
        <Container>
          <Row>
            {currentTours?.map((tour) => (
              <Col lg='3' className='mb-4' key={tour.id}>
                <TourCard tour={tour} />
              </Col>
            ))}
            <Col lg='12'>
              <div className="pagination d-flex align-items-center justify-content-center mt-4 gap-3">
                {[...Array(pageCount).keys()].map((number) => (
                  <span
                    key={number}
                    onClick={() => setPage(number)}
                    className={page === number ? 'active__page' : ''}
                  >
                    {number + 1}
                  </span>
                ))}
              </div>
            </Col>
          </Row>
        </Container>
      </section>
      <Newsletter />
    </>
  );
};

export default Tours;
